// frontend/src/pages/Resume.js
import React, { useState } from 'react';
import { Download, Mail, Phone, MapPin, Globe, Calendar, Award, GraduationCap, Briefcase, Upload, X, User, BookOpen, Star } from 'lucide-react';

const Resume = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState('');
  const [uploadError, setUploadError] = useState('');

  const education = [
    {
      degree: 'Bachelor of Commerce',
      specialization: 'Accounting',
      school: 'Mumbai University',
      duration: '2023',
      score: '7.48 GPA',
      achievements: [
        'Specialized in Accounting',
        'Completed degree with strong academic performance'
      ]
    },
    {
      degree: 'HSC',
      specialization: 'Commerce',
      school: 'Maharashtra State Board',
      duration: '2020',
      score: '44.46%',
      achievements: [
        'Commerce stream with focus on business studies'
      ]
    },
    {
      degree: 'SSC',
      specialization: 'General Education',
      school: 'Maharashtra State Board',
      duration: '2018',
      score: '56%',
      achievements: [
        'Completed secondary education with good marks'
      ]
    }
  ];

  const certifications = [
    {
      name: 'MS-CIT',
      issuer: 'Yuva Parivartan Classes',
      date: '2023',
      credential: 'Computer Fundamentals Certification'
    },
    {
      name: 'Tally ERP 9 and GST',
      issuer: 'Omkar Classes',
      date: '2024',
      credential: 'Accounting Software Certification'
    },
    {
      name: 'Advance Excel',
      issuer: 'Omkar Classes',
      date: '2024',
      credential: 'Advanced Spreadsheet Skills'
    }
  ];

  const skills = {
    'Computer Skills': ['MS Office Suite', 'Tally ERP 9', 'Internet Operations', 'Advanced Excel'],
    'Accounting Skills': ['Bookkeeping', 'GST Filing', 'Financial Records', 'Data Analysis'],
    'Soft Skills': ['Communication', 'Teamwork', 'Adaptability', 'Quick Learning']
  };

  const interests = [
    'Cricket', 'Badminton', 'Reading', 'Music', 'Travelling'
  ];

  // File selection handler
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validate file type (PDF only)
      if (file.type !== 'application/pdf') {
        setUploadError('Please select a PDF file only');
        return;
      }
      
      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setUploadError('File size must be less than 5MB');
        return;
      }
      
      setSelectedFile(file);
      setUploadError('');
      setUploadStatus('');
    }
  };

  // File upload handler
  const handleUpload = async () => {
    if (!selectedFile) {
      setUploadError('Please select a file first');
      return;
    }
    
    setIsUploading(true);
    setUploadStatus('Uploading...');
    setUploadError('');
    
    try {
      const formData = new FormData();
      formData.append('resume', selectedFile);
      
      // This would be replaced with your actual API endpoint
      const response = await fetch('/api/upload-resume', {
        method: 'POST',
        body: formData,
      });
      
      if (response.ok) {
        setUploadStatus('Upload successful!');
        setTimeout(() => {
          setUploadStatus('');
          setSelectedFile(null);
        }, 3000);
      } else {
        throw new Error('Upload failed');
      }
    } catch (error) {
      console.error('Upload error:', error);
      setUploadError('Upload failed. Please try again.');
    } finally {
      setIsUploading(false);
    }
  };

  // Remove selected file
  const removeFile = () => {
    setSelectedFile(null);
    setUploadError('');
    setUploadStatus('');
  };

  return (
    <div className="min-h-screen py-12 bg-gray-50 dark:bg-gray-900">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-10 bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg">
          <h1 className="text-4xl md:text-5xl font-bold mb-2 text-gray-800 dark:text-white">
            MILIND JAGDISH PATIL
          </h1>
          <p className="text-lg text-gray-600 dark:text-gray-300 mb-6">
            Accounting Professional | Tally & Excel Certified
          </p>
          
          <div className="flex flex-col sm:flex-row gap-4 justify-center mb-6">
            <a
              href="/api/resume"
              download="Milind_Patil_Resume.pdf"
              className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-semibold transition-colors inline-flex items-center justify-center"
            >
              <Download size={20} className="mr-2" />
              Download Resume
            </a>
            <a
              href="mailto:milindpati0000@gmail.com"
              className="border-2 border-blue-600 text-blue-600 hover:bg-blue-600 hover:text-white px-6 py-3 rounded-lg font-semibold transition-colors inline-flex items-center justify-center"
            >
              <Mail size={20} className="mr-2" />
              Email Me
            </a>
          </div>

          {/* Resume Upload Section */}
          <div className="mt-8 pt-6 border-t border-gray-200 dark:border-gray-700">
            <h2 className="text-xl font-bold mb-4 text-gray-800 dark:text-white">Update Your Resume</h2>
            <div className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-4 text-center">
              {!selectedFile ? (
                <>
                  <Upload className="mx-auto text-gray-400 mb-3" size={32} />
                  <p className="text-gray-500 dark:text-gray-400 mb-3 text-sm">
                    Upload a new PDF resume to replace the current one
                  </p>
                  <label htmlFor="resume-upload" className="cursor-pointer bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg font-semibold transition-colors inline-block text-sm">
                    Select PDF File
                  </label>
                  <input
                    id="resume-upload"
                    type="file"
                    accept=".pdf,application/pdf"
                    className="hidden"
                    onChange={handleFileSelect}
                  />
                </>
              ) : (
                <>
                  <div className="flex items-center justify-between bg-gray-100 dark:bg-gray-700 p-3 rounded-md mb-4">
                    <div className="flex items-center">
                      <div className="bg-blue-100 dark:bg-blue-900 p-2 rounded mr-3">
                        <Download className="text-blue-600 dark:text-blue-300" size={16} />
                      </div>
                      <div className="text-left">
                        <p className="font-medium truncate max-w-xs text-sm">{selectedFile.name}</p>
                        <p className="text-xs text-gray-500 dark:text-gray-400">
                          {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                        </p>
                      </div>
                    </div>
                    <button 
                      onClick={removeFile} 
                      className="text-gray-500 hover:text-red-500 p-1"
                      disabled={isUploading}
                    >
                      <X size={16} />
                    </button>
                  </div>
                  <button
                    onClick={handleUpload}
                    disabled={isUploading}
                    className={`bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg font-semibold transition-colors text-sm ${isUploading ? 'opacity-50 cursor-not-allowed' : ''}`}
                  >
                    {isUploading ? (
                      <span className="flex items-center">
                        <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                        Uploading...
                      </span>
                    ) : (
                      'Upload Resume'
                    )}
                  </button>
                </>
              )}
              
              {uploadStatus && (
                <p className="text-green-600 dark:text-green-400 mt-3 text-sm">{uploadStatus}</p>
              )}
              
              {uploadError && (
                <p className="text-red-600 dark:text-red-400 mt-3 text-sm">{uploadError}</p>
              )}
              
              <p className="text-xs text-gray-500 dark:text-gray-400 mt-3">
                Maximum file size: 5MB. PDF format only.
              </p>
            </div>
          </div>
        </div>

        {/* Contact Information */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold mb-4 flex items-center">
            <User className="text-blue-600 mr-3" size={24} />
            Contact Information
          </h2>
          <div className="grid md:grid-cols-2 gap-4">
            <div className="flex items-center">
              <Mail className="text-blue-600 mr-3" size={20} />
              <span>milindpati0000@gmail.com</span>
            </div>
            <div className="flex items-center">
              <Phone className="text-blue-600 mr-3" size={20} />
              <span>9764153013</span>
            </div>
            <div className="flex items-center">
              <MapPin className="text-blue-600 mr-3" size={20} />
              <span>At. Post. Jambrung, Khalapur, Raigad, Maharashtra-410203</span>
            </div>
          </div>
        </div>

        {/* Professional Summary */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold mb-4 flex items-center">
            <Star className="text-blue-600 mr-3" size={24} />
            Summary
          </h2>
          <p className="text-gray-600 dark:text-gray-300 leading-relaxed">
            A diligent and motivated individual with a B.Com in Accounting, seeking to leverage acquired skills and certifications in a challenging role. Possesses strong technical skills in MS-CIT, Tally ERP 9, and Advanced Excel, complemented by a proactive approach to learning and a keen interest in outdoor activities and music.
          </p>
        </div>

        {/* Objective */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold mb-4">Career Objective</h2>
          <p className="text-gray-600 dark:text-gray-300 leading-relaxed">
            To secure a challenging position in a reputable organization where I can utilize my skills and contribute to the company's growth.
          </p>
        </div>

        {/* Education */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <div className="flex items-center mb-6">
            <GraduationCap className="text-blue-600 mr-3" size={24} />
            <h2 className="text-2xl font-bold">Academic Qualifications</h2>
          </div>
          
          <div className="space-y-6">
            {education.map((edu, index) => (
              <div key={index} className="border-l-2 border-green-500 pl-6">
                <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-2">
                  <h3 className="text-xl font-bold">{edu.degree}</h3>
                  <div className="flex items-center text-gray-500 dark:text-gray-400">
                    <Calendar size={16} className="mr-1" />
                    {edu.duration}
                  </div>
                </div>
                <div className="flex items-center mb-3">
                  <p className="text-green-600 font-semibold mr-3">{edu.school}</p>
                  <span className="text-gray-500 dark:text-gray-400">{edu.score}</span>
                </div>
                <p className="text-gray-600 dark:text-gray-400 mb-2">{edu.specialization}</p>
                <ul className="space-y-1">
                  {edu.achievements.map((achievement, achIndex) => (
                    <li key={achIndex} className="flex items-start">
                      <div className="w-2 h-2 bg-green-500 rounded-full mr-3 mt-2 flex-shrink-0"></div>
                      <span className="text-gray-600 dark:text-gray-300">{achievement}</span>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>

        {/* Certifications */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <div className="flex items-center mb-6">
            <Award className="text-blue-600 mr-3" size={24} />
            <h2 className="text-2xl font-bold">Certifications</h2>
          </div>
          
          <div className="grid md:grid-cols-2 gap-6">
            {certifications.map((cert, index) => (
              <div key={index} className="border border-gray-200 dark:border-gray-600 p-4 rounded-lg">
                <h3 className="font-bold mb-2">{cert.name}</h3>
                <p className="text-blue-600 mb-1">{cert.issuer}</p>
                <div className="flex justify-between items-center text-sm text-gray-500 dark:text-gray-400">
                  <span>Completed: {cert.date}</span>
                  <span>{cert.credential}</span>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Skills */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold mb-6">Skills</h2>
          
          <div className="grid md:grid-cols-2 gap-6">
            {Object.entries(skills).map(([category, skillList], index) => (
              <div key={index}>
                <h3 className="text-lg font-semibold mb-3 text-blue-600">{category}</h3>
                <div className="flex flex-wrap gap-2">
                  {skillList.map((skill, skillIndex) => (
                    <span
                      key={skillIndex}
                      className="px-3 py-1 bg-blue-100 dark:bg-blue-900 text-blue-600 dark:text-blue-300 rounded-full text-sm font-medium"
                    >
                      {skill}
                    </span>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Interests */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold mb-6">Interests</h2>
          <div className="flex flex-wrap gap-3">
            {interests.map((interest, index) => (
              <span
                key={index}
                className="px-4 py-2 bg-green-100 dark:bg-green-900 text-green-600 dark:text-green-300 rounded-full font-medium"
              >
                {interest}
              </span>
            ))}
          </div>
        </div>

        {/* Personal Details */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold mb-6">Personal Details</h2>
          <div className="grid md:grid-cols-2 gap-4">
            <div className="flex justify-between items-center">
              <span className="font-semibold">Full Name</span>
              <span className="text-gray-600 dark:text-gray-300">Milind Jagdish Patil</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="font-semibold">Date of Birth</span>
              <span className="text-gray-600 dark:text-gray-300">10 May 2003</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="font-semibold">Languages Known</span>
              <span className="text-gray-600 dark:text-gray-300">English, Hindi, Marathi</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="font-semibold">Hobbies</span>
              <span className="text-gray-600 dark:text-gray-300">Outdoor games, Music, Travelling</span>
            </div>
          </div>
        </div>

        {/* Declaration */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold mb-4">Declaration</h2>
          <p className="text-gray-600 dark:text-gray-300 mb-4">
            I hereby declare that the above mentioned details are true as per my belief and knowledge.
          </p>
          <div className="flex justify-between items-center mt-6">
            <div>
              <span className="font-semibold">Place: ______</span>
            </div>
            <div>
              <span className="font-semibold">Date: ______</span>
            </div>
          </div>
        </div>

        {/* Call to Action */}
        <div className="text-center">
          <div className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-gray-800 dark:to-gray-700 p-8 rounded-xl">
            <h3 className="text-2xl font-bold mb-4">Interested in working together?</h3>
            <p className="text-gray-600 dark:text-gray-300 mb-6">
              I'm always open to discussing new opportunities and accounting projects.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <a
                href="mailto:milindpati0000@gmail.com"
                className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-3 rounded-lg font-semibold transition-colors inline-flex items-center justify-center"
              >
                <Mail size={20} className="mr-2" />
                Contact Me
              </a>
              <a
                href="tel:9764153013"
                className="border-2 border-blue-600 text-blue-600 hover:bg-blue-600 hover:text-white px-8 py-3 rounded-lg font-semibold transition-colors inline-flex items-center justify-center"
              >
                <Phone size={20} className="mr-2" />
                Call Me
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Resume;
