// frontend/src/pages/Resume.js
import React, { useState } from 'react';
import { Download, Mail, Phone, MapPin, Globe, Calendar, Award, GraduationCap, Briefcase, Upload, X } from 'lucide-react';

const Resume = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState('');
  const [uploadError, setUploadError] = useState('');

  const education = [
    // ... (keep your existing education data)
  ];

  const experience = [
    // ... (keep your existing experience data)
  ];

  const skills = {
    // ... (keep your existing skills data)
  };

  const certifications = [
    // ... (keep your existing certifications data)
  ];

  // File selection handler
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validate file type (PDF only)
      if (file.type !== 'application/pdf') {
        setUploadError('Please select a PDF file only');
        return;
      }
      
      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setUploadError('File size must be less than 5MB');
        return;
      }
      
      setSelectedFile(file);
      setUploadError('');
      setUploadStatus('');
    }
  };

  // File upload handler
  const handleUpload = async () => {
    if (!selectedFile) {
      setUploadError('Please select a file first');
      return;
    }
    
    setIsUploading(true);
    setUploadStatus('Uploading...');
    setUploadError('');
    
    try {
      const formData = new FormData();
      formData.append('resume', selectedFile);
      
      const response = await fetch('/api/upload-resume', {
        method: 'POST',
        body: formData,
      });
      
      if (response.ok) {
        setUploadStatus('Upload successful!');
        setTimeout(() => {
          setUploadStatus('');
          setSelectedFile(null);
        }, 3000);
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Upload failed');
      }
    } catch (error) {
      console.error('Upload error:', error);
      setUploadError(error.message || 'Upload failed. Please try again.');
    } finally {
      setIsUploading(false);
    }
  };

  // Remove selected file
  const removeFile = () => {
    setSelectedFile(null);
    setUploadError('');
    setUploadStatus('');
  };

  return (
    <div className="min-h-screen py-20">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-bold mb-6">
            My <span className="text-blue-600">Resume</span>
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300 mb-8">
            Download my complete resume or upload a new version
          </p>
          
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <a
              href="/api/resume"
              download="John_Doe_Resume.pdf"
              className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-3 rounded-lg font-semibold transition-colors inline-flex items-center justify-center"
            >
              <Download size={20} className="mr-2" />
              Download PDF
            </a>
            <a
              href="mailto:john.doe@example.com"
              className="border-2 border-blue-600 text-blue-600 hover:bg-blue-600 hover:text-white px-8 py-3 rounded-lg font-semibold transition-colors inline-flex items-center justify-center"
            >
              <Mail size={20} className="mr-2" />
              Email Me
            </a>
          </div>
        </div>

        {/* Resume Upload Section */}
        <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold mb-4">Update Your Resume</h2>
          <div className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-6 text-center">
            {!selectedFile ? (
              <>
                <Upload className="mx-auto text-gray-400 mb-4" size={40} />
                <p className="text-gray-500 dark:text-gray-400 mb-4">
                  Upload a new PDF resume to replace the current one
                </p>
                <label htmlFor="resume-upload" className="cursor-pointer bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg font-semibold transition-colors inline-block">
                  Select PDF File
                </label>
                <input
                  id="resume-upload"
                  type="file"
                  accept=".pdf,application/pdf"
                  className="hidden"
                  onChange={handleFileSelect}
                />
              </>
            ) : (
              <>
                <div className="flex items-center justify-between bg-gray-100 dark:bg-gray-700 p-3 rounded-md mb-4">
                  <div className="flex items-center">
                    <div className="bg-blue-100 dark:bg-blue-900 p-2 rounded mr-3">
                      <Download className="text-blue-600 dark:text-blue-300" size={20} />
                    </div>
                    <div className="text-left">
                      <p className="font-medium truncate max-w-xs">{selectedFile.name}</p>
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                      </p>
                    </div>
                  </div>
                  <button 
                    onClick={removeFile} 
                    className="text-gray-500 hover:text-red-500 p-1"
                    disabled={isUploading}
                  >
                    <X size={20} />
                  </button>
                </div>
                <button
                  onClick={handleUpload}
                  disabled={isUploading}
                  className={`bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg font-semibold transition-colors ${isUploading ? 'opacity-50 cursor-not-allowed' : ''}`}
                >
                  {isUploading ? (
                    <span className="flex items-center">
                      <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Uploading...
                    </span>
                  ) : (
                    'Upload Resume'
                  )}
                </button>
              </>
            )}
            
            {uploadStatus && (
              <p className="text-green-600 dark:text-green-400 mt-4">{uploadStatus}</p>
            )}
            
            {uploadError && (
              <p className="text-red-600 dark:text-red-400 mt-4">{uploadError}</p>
            )}
            
            <p className="text-xs text-gray-500 dark:text-gray-400 mt-4">
              Maximum file size: 5MB. PDF format only.
            </p>
          </div>
        </div>

        {/* The rest of your existing resume content remains unchanged */}
        {/* Contact Information */}
